<?php/* Array para el INDEX principal $mapArchivos//*'Feed' => array( $NomControl =>'ctrlFeed',                            $rutaModel  =>__MODEL__."cdt.Feed.php",                            $rutaCtrl   =>__CONTROLLER__."ctrl.Feed.php" ,                            $rutaView   =>__MODULES__.'viewFeed.php'),// agregregar al sitio princial $mapPaginas 'Feed'      => 'Feed'," & Chr(13)*/require_once __CONTROLLER__."mvc.ctrlPostEntidad.php";require_once __CONTROLLER__."mvc.classCtrlInterface.php";class ctrlFeed extends ctrlPostEntidad  // mvcClassCtrl implements CtrlClassInterface{Public Function Inicia(){ $this->LayOutPagina      = 'principal.php'; /* Cuando el usario no tiene opciones*/ $this->sectionsMENULEFT  = ""; $this->CSSADICIONALES  = ""; $this->CSSADICIONALES  .= "customTabs.css|"; $this->CSSADICIONALES  .= "jsgrid.min.css|"; $this->CSSADICIONALES  .= "jsgrid-theme.min.css|"; $this->CSSADICIONALES  .= "external-pager.css|"; $this->SCRIPTADICIONALES  = ""; $this->SCRIPTADICIONALES  .= "jsgrid.min.js|system.jsgrid.js|"; $this->SCRIPTADICIONALES  .= "spin.min.js|spin.conf.js|"; //$this->SCRIPTADICIONALES  .= "jquery-1.7.1.min.js|"; //$this->SCRIPTADICIONALES  .= "jquery-ui.min.js|"; $this->SCRIPTADICIONALES  .= "system.js|"; $CapertaFeed      = "Feeds/"; /* CORREGIR poner en Plural*/ $this->SCRIPTADICIONALES  .= $CapertaFeed."system.GridFeeds.avr.js|"; $this->SCRIPTADICIONALES  .= $CapertaFeed."system.GridFeeds.Botones.js|"; $this->SCRIPTADICIONALES  .= $CapertaFeed."system.GridFeeds.Core.js|"; $this->SCRIPTADICIONALES  .= $CapertaFeed."system.GridFeeds.js|"; $this->SCRIPTADICIONALES  .= $CapertaFeed."system.Feeds.js|"; $html=$this->BusquedaConPaginacion(); $this->IniciaContexto('Feed  En la Base de datos',$html);}public function BusquedaConPaginacion( $txtsearch="", $pagina=0, $tamano=10  ){ /*En esta seccion hay que validar las listas u opciones que se desplegaran */ $verFeed = new viewFeed(); $html = $verFeed->ListadoConbuscador( ); unset($verFeed); return $html;}public function ver(  ){  /* instancia a modelo que realiza la busqueda*/  $VerUnFeed = new Feed(); /* Obtenemos el ID, que nos traera los datos de la clase Feed */ /*$id =$this->GetIDByPost(); // Esto es si es una clase sola*/  $id=$this->GetVarByPost($VerUnaFeed->CampoID); /* Obtenemos los datos de la Feed*/ $sData = $VerUnFeed->GetBaseById($id); /* Instancia de la clase que visaliza los datos */  $tablaFeed =new viewFeed(); /* Si no hay datos imprimos un mensage  */ if( $sData == null )    echo " No se encontro el elememto buscado, consulte al soporte tecnico"; /* Obtenemos la tabla en html con los datos de la Feed */ $htmlTablaFeed =  $tablaFeed->ImprimeFeed( $sData ); $this->view_page($htmlTablaFeed); /* Liberar memoria */ unset($sData); unset($tablaFeed); unset($VerUnFeed);}}//--> fin clase?>