<?php/* Array para el INDEX principal $mapArchivos//*'Noticia' => array( $NomControl =>'ctrlNoticia',                            $rutaModel  =>__MODEL__."cdt.Noticia.php",                            $rutaCtrl   =>__CONTROLLER__."ctrlNoticia.php" ,                            $rutaView   =>__MODULES__.'viewNoticia.php'),// agregregar al sitio princial $mapPaginas 'Noticia'      => 'Noticia'," & Chr(13)*/require_once __CONTROLLER__."mvc.ctrlPostEntidad.php";require_once __CONTROLLER__."mvc.classCtrlInterface.php";class ctrlNoticia extends ctrlPostEntidad  // mvcClassCtrl implements CtrlClassInterface{Public Function Inicia(){ $this->LayOutPagina      = 'principal.php'; /* Cuando el usario no tiene opciones*/ //$this->sectionsMENULEFT  = ""; $this->CSSADICIONALES  = ""; $this->CSSADICIONALES  .= "customTabs.css|"; $this->CSSADICIONALES  .= "jsgrid.min.css|"; $this->CSSADICIONALES  .= "jsgrid-theme.min.css|"; $this->CSSADICIONALES  .= "external-pager.css|"; $this->SCRIPTADICIONALES  = ""; $this->SCRIPTADICIONALES  .= "jsgrid.min.js|system.jsgrid.js|"; $this->SCRIPTADICIONALES  .= "spin.min.js|spin.conf.js|"; //$this->SCRIPTADICIONALES  .= "jquery-1.7.1.min.js|"; //$this->SCRIPTADICIONALES  .= "jquery-ui.min.js|"; $this->SCRIPTADICIONALES  .= "system.js|"; $CapertaNoticias      = "Noticias/"; /* CORREGIR LOS SCRIPT*/ $this->SCRIPTADICIONALES  .= $CapertaNoticias."system.GridNoticias.avr.js|"; $this->SCRIPTADICIONALES  .= $CapertaNoticias."system.GridNoticias.Botones.js|"; $this->SCRIPTADICIONALES  .= $CapertaNoticias."system.GridNoticias.Core.js|"; $this->SCRIPTADICIONALES  .= $CapertaNoticias."system.GridNoticias.js|"; $this->SCRIPTADICIONALES  .= $CapertaNoticias."system.Noticias.js|"; $html=$this->BusquedaConPaginacion(); $this->IniciaContexto('Noticia  En la Base de datos',$html);}public function BusquedaConPaginacion( $txtsearch="", $pagina=0, $tamano=10  ){ /*En esta seccion hay que validar las listas u opciones que se desplegaran */ $verNoticia = new viewNoticia(); $html = $verNoticia->ListadoConbuscador( ); unset($verNoticia); return $html;}public function ver(  ){  /* instancia a modelo que realiza la busqueda*/  $VerUnNoticia = new Noticia(); /* Obtenemos el ID, que nos traera los datos de la clase Noticia */ /*$id =$this->GetIDByPost(); // Esto es si es una clase sola*/  $id=$this->GetVarByPost($VerUnaNoticia->CampoID); /* Obtenemos los datos de la Noticia*/ $sData = $VerUnNoticia->GetBaseById($id); /* Instancia de la clase que visaliza los datos */  $tablaNoticia =new viewNoticia(); /* Si no hay datos imprimos un mensage  */ if( $sData == null )    echo " No se encontro el elememto buscado, consulte al soporte tecnico"; /* Obtenemos la tabla en html con los datos de la Noticia */ $htmlTablaNoticia =  $tablaNoticia->ImprimeNoticia( $sData ); $this->view_page($htmlTablaNoticia); /* Liberar memoria */ unset($sData); unset($tablaNoticia); unset($VerUnNoticia);}}//--> fin clase?>